import scanpy as sc import muon as mu from muon import atac as ac import pandas as pd atac_data = sc.read_h5ad("$atac_input")
result = atac.uns['rank_genes_groups'] groups = result['names'].dtype.names pd.set_option("max_columns", 50) genes_list = pd.DataFrame( {group + '_' + key[:1]: result[key][group] for group in groups for key in ['names', 'genes', 'pvals']}).head(10)
atac_data.write("atac_with_marker_genes_and_celltypes.h5ad") genes_list.to_csv('marker_genes_and_celltypes.csv')
